from utilities import *
codon_table_raw = '''UUU F      CUU L      AUU I      GUU V
UUC F      CUC L      AUC I      GUC V
UUA L      CUA L      AUA I      GUA V
UUG L      CUG L      AUG M      GUG V
UCU S      CCU P      ACU T      GCU A
UCC S      CCC P      ACC T      GCC A
UCA S      CCA P      ACA T      GCA A
UCG S      CCG P      ACG T      GCG A
UAU Y      CAU H      AAU N      GAU D
UAC Y      CAC H      AAC N      GAC D
UAA Stop   CAA Q      AAA K      GAA E
UAG Stop   CAG Q      AAG K      GAG E
UGU C      CGU R      AGU S      GGU G
UGC C      CGC R      AGC S      GGC G
UGA Stop   CGA R      AGA R      GGA G
UGG W      CGG R      AGG R      GGG G '''

codon_table = codon_table_raw.strip().split()
codon_dict ={}
label = ''
for index, transl in enumerate(codon_table):
    if index % 2 == 0:
        label = transl
        codon_dict[transl] = ''
    else:
        codon_dict[label] = transl
# return codon_dict
def translate(seq, colors=True, pos_ini=0):
    # return [codon_dict[seq[cod:cod+3]] for cod in range(pos_ini, len(seq)-2,3)]

    '''Receives RNA string and return correspondant protein sequence'''
    ptn = ''
    codon = ''
    cont = 0
    for read in seq:
        if cont < 3:
            codon += read
            cont += 1
        if cont == 3:
            cont = 0
            for key, value in codon_dict.items():
                if codon == key:
                    ptn += value
                    codon = ''
    ptn = ptn.replace('Stop','')
    if colors:
        return DRNAPTNcolors(ptn)
    else:
        return ptn
print(translate(''))